void main() {
  WidgetsFlutterBinding.ensureInitialized(); // Ensures async initialization is handled
  runApp(const MyApp());
}


class MyApp extends StatelessWidget {
  const MyApp({super.key});
  
  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      debugShowCheckedModeBanner: false,
      title: 'Auth App',
      home: const LoginScreen(),
      theme: ThemeData(
        primarySwatch: Colors.purple,
      ),
      initialRoute: '/splash',
      routes: {
        '/splash': (context) => const SplashScreen(),
        '/intro': (context) => const SlideScreen(),
        '/welcome': (context) => const WelcomeScreen(), // Add welcome screen route
        '/login': (context) => const LoginScreen(), // Add login screen route
        '/home': (context) => const HomePage(),
        '/forgot_password': (context) => const ForgotPassword(),
        '/spell_grade': (context) => const SpellGradePage(),
        '/signup': (context) => const SignupScreen(), // Add this line in routes
      },
    );
  }
}
